/* Box Model Hack */
* {
     -moz-box-sizing: border-box; /* Firexfox */
     -webkit-box-sizing: border-box; /* Safari/Chrome/iOS/Android */
     box-sizing: border-box; /* IE */
}

/* Clear fix hack */
.clearfix:after {
     content: ".";
     display: block;
     clear: both;
     visibility: hidden;
     line-height: 0;
     height: 0;
}

.clear {
  clear: both;
}

.alignright {
  float: right; 
  padding: 0 0 10px 10px; /* note the padding around a right floated image */
}

.alignleft {
  float: left; 
  padding: 0 10px 10px 0; /* note the padding around a left floated image */
}

body {
     color: #000;
     font-size: 12px;
     line-height: 1.4;
     font-family: Helvetica, Arial, sans-serif;
}

header {
  text-align: center;
}

#mainarea {
     text-align: center;
     margin-bottom: 50px;
     margin-top: 30px;
}

#prompt {
     background: #fece17;
}

#usertext {
     background: #99ff99;
}

#resultarea {
     font-size: 24px;
     border: 3px dotted gray;
}

button {
     color: white;
     font-size: 24px;
     background: blue;
}

$(document).ready(function() {
    var startTime;

    $("#start-easy").click(easyTest);
    $("#donebutton").click(finishTest);

    function easyTest() {
        $('#prompt').val('lorem ipsum sic dolor');
        startTime = new Date();
    }

    function finishTest() {
        endTime = new Date();
        var userText = $("#usertext").val();
        var totalTime = Math.round((endTime - startTime)/1000);
        var words = userText.split(' ');

        var typingspeed = Math.round(words.length/totalTime * 60);

        $('#resulttext').html(
            'You typed '+ words.length + ' words in '
            + totalTime + ' seconds, a speed of about '
            + typingspeed +' words per minute!'
        );
    }
});

